import { useState } from 'react'
import "./testimonial.css"
import { RiSubtractFill, RiArrowRightLine, RiArrowLeftLine } from "react-icons/ri";
import Agent from './Agent';
import { client1, client2, client3, client4 } from './index';




const Testimonial = () => {

  const [showCarousol, setShowCarousol] = useState(false);
  const neme = ["d-flex"]


  // slide-out-right
  // slide-out-left 

  const setHandleShowCarousol = () => {
    setShowCarousol(false)
    // neme = slide-out-left

  }


  return (
    <div className="la-maison__testimonial py-5">
      <div className="la-maison__testimonial-head">
        <div>
          <h2 className=''><RiSubtractFill size={50} />  testimonials</h2>
          <h3>What Our Clients Say About Us</h3>
        </div>
        <div>
          {showCarousol ?
            <RiArrowLeftLine size={30} 
            onClick={setHandleShowCarousol}/> 
          :
          <RiArrowLeftLine size={30} 
          onClick={() => setShowCarousol(true)}/> 
          }

          {!showCarousol ?
            <RiArrowRightLine size={30} 
          onClick={() => setShowCarousol(true)}/>
          : <RiArrowRightLine size={30} 
          onClick={() => setShowCarousol(false)}/>
          }
        </div>

      </div>
      <div className="la-maison__testimonial-body">
        {!showCarousol && <div className={neme}>
          <Agent
            img={client3}
            title="Fikayo Tomori"
            text="I am very impressed with the service of La Maison. I’d definitely recommend them to my buddies."
            career="Footballer" />

          <Agent
            img={client4}
            title="Anne Harlow"
            text="I am very impressed with the service of La Maison. I’d definitely recommend them to my buddies."
            career="Actress" />
        </div>}


        {showCarousol && <div className='d-flex'>
          <Agent
            img={client1}
            title="Fikayo Tomori"
            text="I am very impressed with the service of La Maison. I’d definitely recommend them to my buddies."
            career="Footballer" />

          <Agent
            img={client2}
            title="Fikayo Tomori"
            text="I am very impressed with the service of La Maison. I’d definitely recommend them to my buddies."
            career="Footballer" />
        </div>}
      </div>

    </div>
  )
}

export default Testimonial;
